// ( ... <script> 标签内的逻辑保持不变 ... )
export default {
  name: 'UserDepositAddressesView',
  data() {
    return {
      loading: false,
      tableData: [],
      totalItems: 0,
      pagination: {
        page: 1,
        limit: 10
      },
      searchParams: {
        userId: '',
        username: '',
        tronAddress: '',
        evmAddress: '',
        pathIndex: null
      }
    };
  },
  created() {
    this.fetchAddresses();
  },
  methods: {
    async fetchAddresses() {
      if (this.loading) return;
      this.loading = true;
      try {
        const params = {
          ...this.pagination,
          userId: this.searchParams.userId || undefined,
          username: this.searchParams.username || undefined,
          tronAddress: this.searchParams.tronAddress || undefined,
          evmAddress: this.searchParams.evmAddress || undefined,
          pathIndex: this.searchParams.pathIndex || undefined
        };
        const response = await this.$api.getUserDepositAddresses(params);
        this.tableData = response.list;
        this.totalItems = response.total;
      } catch (error) {
        console.error('Failed to fetch user addresses:', error);
      } finally {
        this.loading = false;
      }
    },
    handleSearch() {
      this.pagination.page = 1;
      this.fetchAddresses();
    },
    handleSizeChange(newLimit) {
      this.pagination.limit = newLimit;
      this.pagination.page = 1;
      this.fetchAddresses();
    },
    handlePageChange(newPage) {
      this.pagination.page = newPage;
      this.fetchAddresses();
    }
  }
};
