// ( ... <script> 标签内的逻辑保持不变 ... )
import { ElMessage, ElMessageBox } from 'element-plus';
export default {
  name: 'BlockedRegionsView',
  data() {
    return {
      loading: true,
      addLoading: false,
      tableData: [],
      addForm: {
        ip_range: '',
        description: ''
      },
      addFormRules: {
        ip_range: [{
          required: true,
          message: '请输入 IP / CIDR',
          trigger: 'blur'
        }, {
          pattern: /^([0-9]{1,3}\.){3}[0-9]{1,3}(\/([0-9]|[1-2][0-9]|3[0-2]))?$/,
          message: '格式错误，请使用 CIDR (e.g., 1.2.3.4/32 or 1.2.3.0/24)',
          trigger: 'blur'
        }]
      }
    };
  },
  created() {
    this.fetchRegions();
  },
  methods: {
    async fetchRegions() {
      this.loading = true;
      try {
        this.tableData = await this.$api.getBlockedRegions();
      } catch (error) {
        console.error('Failed to fetch blocked regions:', error);
      } finally {
        this.loading = false;
      }
    },
    async handleAddRegion() {
      const formEl = this.$refs.addFormRef;
      if (!formEl) return;
      await formEl.validate(async valid => {
        if (valid) {
          this.addLoading = true;
          try {
            await this.$api.addBlockedRegion(this.addForm);
            ElMessage.success('阻挡地区新增成功');
            formEl.resetFields(); // 清空表单
            await this.fetchRegions(); // 刷新列表
          } catch (error) {
            console.error('Failed to add region:', error);
          } finally {
            this.addLoading = false;
          }
        } else {
          return false;
        }
      });
    },
    handleDelete(row) {
      ElMessageBox.confirm(`确定要刪除阻挡规則 "${row.ip_range}" 吗？`, '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          await this.$api.deleteBlockedRegion(row.id);
          ElMessage.success('阻挡规則刪除成功');
          await this.fetchRegions(); // 刷新列表
        } catch (error) {
          console.error('Failed to delete region:', error);
        }
      }).catch(() => {});
    },
    formatDateTime(isoString) {
      if (!isoString) return '';
      try {
        return new Date(isoString).toLocaleString('zh-TW', {
          timeZone: 'Asia/Taipei'
        });
      } catch (e) {
        return isoString;
      }
    }
  }
};
